import React from 'react';
import './App.css';

class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            id : 0,
            word : "",
            translate : "",
            cards : []
        }
    }

    updateInput(key, value) {
        this.setState({
            [key] : value
        })
    }


    addCard() {
        const newCard = {
            id : 1 + this.state.id,
            value : {
                word : this.state.word.slice(),
                translate : this.state.translate.slice(),
                overturned : false
            }
        };

        this.setState({
            id : newCard.id,
            word : "",
            translate : "",
            cards : [...this.state.cards, newCard]
        })
    }

    turnCard(id) {
        const cards = [...this.state.cards];

        let index = cards.findIndex((card) => {
            return card.id === id
        });

        cards[index].value.overturned = !cards[index].value.overturned;

        this.updateInput("cards", cards);
    }

    render() {
        return (
            <div className="App">
                <h1 className="app-title">Карточки английских слов</h1>
                <div className="container">
                    Добавить карточку...
                    <br/>
                    <input
                        type="text"
                        placeholder="Ваше имя"
                        value={this.state.name}
                        onChange={(e) => this.updateInput("name", e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Ваша фамилия"
                        value={this.state.surname}
                        onChange={(e) => this.updateInput("surname", e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Год рождения"
                        value={this.state.age}
                        onChange={(e) => this.updateInput("age", e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Город проживания"
                        value={this.state.town}
                        onChange={(e) => this.updateInput("town", e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Специализация"
                        value={this.state.specialization}
                        onChange={(e) => this.updateInput("specialization", e.target.value)}
                    />
                    <button
                        className="btn add-btn"
                        onClick={() => this.addCard()}
                    >
                        Добавить
                    </button>
                    <div>
                        {this.state.cards.map(card => {
                            return(
                                <div
                                    key={card.id}
                                    className={"card" + (card.value.overturned ? " overturned" : "")}
                                    onClick={() => this.turnCard(card.id)}
                                >
                                    {card.value.overturned ? card.value.translate : card.value.word}
                                </div>
                            )
                        })}
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
